import React from 'react';
import './App.css';
import Login from './paginas/Login';
import Logout from './paginas/Logout';
import Dashboard from './paginas/Dashboard';
import Home from './paginas/Home';
import Header from "./componentes/Header";
import { BrowserRouter, Switch, Route, NavLink, Link } from 'react-router-dom';
import PrivateRoute from "./componentes/PrivateRoute";
import PublicRoute from "./componentes/PublicRoute1";
import { getToken} from "./utils/Commons";
import PrivateLinkMenu from './componentes/PrivateLinkMenu';
import PublicLinkMenu from './componentes/PublicLinkMenu';


class App extends React.Component{
    state = {
        loggedIn: false
    };

    handleLoggedIn = loggedIn => {
        this.setState({loggedIn});
        this.props.history.push('/accion_social');
    }

    handleLoggedOut = location => {
        this.setState({loggedIn : false});
        window.location.href= '/accion_social/login';
        sessionStorage.clear();
    }

    render () {
        const {loggedIn}=this.state;
        return (
            <div>

                <Header className="App-header" />

                <BrowserRouter>
                    <div>
                        <div className="header">
                            <PublicLinkMenu activeClassName="active" urlto="/accion_social/login" texttourl="Login" loggedIn />
                            <PrivateLinkMenu activeClassName="active" exact urlto="/accion_social" texttourl="Home" loggedIn />
                            {/*<PrivateLinkMenu activeClassName="active" exact urlto="/accion_social/logout" texttourl="Logout" /> */}
                            <a onClick={this.handleLoggedOut} style={{cursor:"pointer"}} >Logout</a>
                        </div>
                        <div className="content">
                            <Switch>
                                <PrivateRoute exact path="/accion_social" component={Home} />
                                <PublicRoute path="/accion_social/login">
                                    <Login onLoggedIn={this.handleLoggedIn }/>
                                </PublicRoute>
                                {/*<PrivateRoute path="/accion_social/logout" >
                                    <Logout onLoggedOut={this.handleLoggedOut }/>
                                </PrivateRoute>*/}
                            </Switch>
                        </div>
                    </div>
                </BrowserRouter>

            </div>
        );
    }
}

export default App;
