import axios from 'axios';

class ServiceConexion {
    apiurl = process.env.REACT_APP_API_URL;

    login = (u, c) => {
        return axios({
            method: 'post',
            url: `${this.apiurl}/Login`,
            headers: { 'content-type': 'application/json' },
            data: { user: u, pass: c }
        });
    };
    
    savepersona = (e) => {
        return axios({
            method: 'post',
            url: `${this.apiurl}/SavePersona`,
            headers: { 'content-type': 'application/json' },
            data: { e }
        });
    };
}

const conn = new ServiceConexion();
export default conn;
