import React from "react";

class Institucion extends React.Component {
    lista_municipal = [ {clave: "BECA", valor: "Beca Estudiantil"}, {clave: "SUBS", valor: "Subsidio"}, {clave: "OTR", valor: "Otro"}];
    lista_provincial = [ {clave: "SUBS", valor: "Subsidio"}, {clave: "TARJ", valor: "Tarjeta Río Negro Presente"}, {clave: "SIP", valor: "Siprove"}, {clave: "OTR", valor: "Otro"}];
    lista_nacional = [ {clave: "AUH", valor: "AUH"}, {clave: "DISC", valor: "Discapacidad"}, {clave: "ALIM", valor: "Alimentar"}, {clave: "PRO", valor: "Progresar"}, {clave: "PEN", valor: "Pensión"}];

    state = {
        institucion: '',
        actividad: '',
        cuit: '',
        responsable: ''
    };

    handleChange = () => {
        
    };
    
    render() {
        return (
            <div className="App">
                <h2>Agregar Institución</h2>
                <div>
                    <form onSubmit={this.handleSubmit}>
                        <label>Nombre</label>
                        <input type="text" id="txtInstitucion" name="institucion" placeholder="Institución" value={this.state.institucion} onChange={e => this.setState({ institucion: e.target.value })} />
                        <label>Apellido</label>
                        <input type="text" id="txtActividad" name="actividad" placeholder="Actividad" value={this.state.actividad} onChange={e => this.setState({ actividad: e.target.value })}/>
                        <label>CUIL / CUIT</label>
                        <input type="text" id="txtCuit" name="cuit" placeholder="CUIL / CUIT" value={this.state.cuit} onChange={e => this.setState({ cuit: e.target.value })}/>
                        <label>Fecha Nacimiento</label>
                        <label>Responsable</label>
                        <select id="ddlResponable" name="responsable" value={this.state.value} onChange={this.handleChange}>
                            <option value="1">Persona 1 - DNI 1</option>
                            <option value="2">Persona 2 - DNI 2</option>
                            <option value="3">Persona 3 - DNI 3</option>
                            <option value="4">Persona 4 - DNI 4</option>
                        </select>
                        <input type="button" value="Guardar" onClick={e => this.handleFormSubmit(e)} />
                    </form>
                </div>
            </div>
        )
    }
}

export default Institucion;
