import React, { useState } from 'react';
import axios from 'axios';
import { setUserSession } from '../utils/Commons';

const API_PATH = 'http://localhost/accion_social/backend/backend.php';

class Login extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            usuario: '',
            clave: '',
            loading: false,
            error: null
        }
    }


    handleFormSubmit = e => {
        //const config = {headers : {"Access-Control-Allow-Origin":"*"}};
        axios({
            method: 'post',
            url: `${API_PATH}`,
            headers: { 'content-type': 'application/json' },
            data: { user: this.state.usuario, pass: this.state.clave, metodo: "Login" }
        })
            .then( response => {  this.setState({loading:false});
                                  setUserSession(response.data.token, response.data.user);
                                  this.props.history.push('accion_social/dashboard');
                               })
            .catch(error => { console.error(error) } );
    };


    render() {
        return (
            <div className="App">
                <p>Acci칩n Social - Inicio de Sesi칩n</p>
                <div>
                    <form>
                        <label>Usuario</label>
                        <input type="text" id="txtUsuario" name="usuario" placeholder="Usuario" value={this.state.usuario} onChange={e => this.setState({ usuario: e.target.value })}/>
                        <label>Contrase침a</label>
                        <input type="password" id="txtClave" name="clave" placeholder="Contrase침a" value={this.state.clave} onChange={e => this.setState({ clave: e.target.value })} />
                        {/*<input type="button" value="Ingresar" onClick={e => this.handleFormSubmit(e)} />*/}
                    </form>
                </div>
                {this.state.error && <><small style={{ color: 'red' }}>{ this.state.error }</small><br /></>}<br />
                <input type="button" value={ this.state.loading ? 'Cargando...' : 'Ingresar'} onClick={ this.handleFormSubmit } disabled={ this.state.loading } /><br />
            </div>
            );
    }
}
export default Login;
