import React from "react";
import { Button, TextField, Grid, MenuItem } from '@material-ui/core';

class Persona extends React.Component {
    
    state = {
        nombre: '',
        apellido: '',
        cuit: '',
        fecha_nacimiento: '',
        edad: '',
        telefono: '',
        email: '',
        calle: '',
        altura: '',
        barrio: '',
        es_beneficiario: '',
        tipo_beneficiario: '',
        tipos_de_beneficiarios: []
    };

    handleChangeBeneficiario = (e) => {
        const es_beneficiario = e.target.value;
        let tipos_de_beneficiarios;

        switch (es_beneficiario) {
            case "NO": tipos_de_beneficiarios = [{clave: "NO", valor: "No Corresponde"}];
                break;
            case "MUNI": tipos_de_beneficiarios = [ {clave: "BECA", valor: "Beca Estudiantil"}, {clave: "SUBS", valor: "Subsidio"}, {clave: "OTR", valor: "Otro"}];
                break;
            case "PROV": tipos_de_beneficiarios = [ {clave: "SUBS", valor: "Subsidio"}, {clave: "TARJ", valor: "Tarjeta Río Negro Presente"}, {clave: "SIP", valor: "Siprove"}, {clave: "OTR", valor: "Otro"}];
                break;
            case "NAC": tipos_de_beneficiarios = [ {clave: "AUH", valor: "Asignación Universal por Hijo"}, {clave: "DISC", valor: "Discapacidad"}, {clave: "ALIM", valor: "Alimentar"}, {clave: "PRO", valor: "Progresar"}, {clave: "PEN", valor: "Pensión"}];
                break;
        }

        this.setState({es_beneficiario: es_beneficiario, tipos_de_beneficiarios: tipos_de_beneficiarios});
    };

    handleChangeTipoBeneficiario = (e) => {
        this.setState({tipo_beneficiario: e.target.value})
    };

    handleFormSubmit = () => {
        
    };
    
    render() {
        return (
            <div className="App">
                <Grid container spacing={3} >
                    <Grid item container justify="flex-start" xs={12}>
                        <h2>Agregar Persona</h2>
                    </Grid>
                    <Grid container spacing={3} item xs={12}>
                        {/*<form onSubmit={this.handleSubmit}>*/}
                        <Grid item xs={6}>
                            <TextField id="txtNombre" fullWidth name="nombre" label="Nombre" value={this.state.nombre} onChange={e => this.setState({ nombre: e.target.value })} ></TextField>
                        </Grid>
                        <Grid item xs={6}>
                            <TextField id="txtApellido" fullWidth name="apellido" label="Apellido" value={this.state.apellido} onChange={e => this.setState({ apellido: e.target.value })} ></TextField>
                        </Grid>
                        <Grid item xs={6}>
                            <TextField id="txtCuit" fullWidth name="cuit" label="CUIL / CUIT" value={this.state.cuit} onChange={e => this.setState({ cuit: e.target.value })} ></TextField>
                        </Grid>
                        <Grid item xs={6}>
                            <TextField id="txtFechaNacimiento" fullWidth name="fecha_nacimiento" label="Fecha Nacimiento" type="date" InputLabelProps={{ shrink: true, }} value={this.state.fecha_nacimiento} onChange={e => this.setState({ fecha_nacimiento: e.target.value })} ></TextField>
                        </Grid>
                        <Grid item xs={6}>
                            <TextField id="txtEdad" fullWidth name="edad" label="Edad" value={this.state.edad} onChange={e => this.setState({ edad: e.target.value })} ></TextField>
                        </Grid>
                        <Grid item xs={6}>
                            <TextField id="txtTelefono" fullWidth name="telefono" label="Teléfono" value={this.state.telefono} onChange={e => this.setState({ telefono: e.target.value })} ></TextField>
                        </Grid>
                        <Grid item xs={6}>
                            <TextField id="txtEmail" fullWidth name="email" label="Email" value={this.state.email} onChange={e => this.setState({ email: e.target.value })} ></TextField>
                        </Grid>
                        <Grid item xs={6}>
                            <TextField id="txtCalle" fullWidth name="calle" label="Calle" value={this.state.calle} onChange={e => this.setState({ calle: e.target.value })} ></TextField>
                        </Grid>
                        <Grid item xs={6}>
                            <TextField id="txtAltura" fullWidth name="altura" label="Altura" value={this.state.altura} onChange={e => this.setState({ altura: e.target.value })} ></TextField>
                        </Grid>
                        <Grid item xs={6}>
                            <TextField id="txtBarrio" fullWidth name="barrio" label="Barrio" value={this.state.barrio} onChange={e => this.setState({ barrio: e.target.value })} ></TextField>
                        </Grid>
                        <Grid item xs={6}>
                            <TextField select fullWidth
                                label="¿Es Beneficiario?"
                                id="ddlBeneficiario"
                                value={this.state.es_beneficiario}
                                onChange={this.handleChangeBeneficiario}
                                >
                                <MenuItem value={"NO"}>No corresponde</MenuItem>
                                <MenuItem value={"MUNI"}>Municipal</MenuItem>
                                <MenuItem value={"PROV"}>Provincial</MenuItem>
                                <MenuItem value={"NAC"}>Nacional</MenuItem>
                            </TextField>
                        </Grid>
                        <Grid item xs={6}>
                            <TextField select fullWidth
                                label="Tipo Beneficio"
                                id="ddlTipoBeneficiario"
                                value={this.state.tipo_beneficiario}
                                onChange={this.handleChangeTipoBeneficiario}
                                >
                                {
                                    this.state.tipos_de_beneficiarios.map(data=><MenuItem key={data.clave} value={data.clave}>{data.valor}</MenuItem>)
                                }
                            </TextField>                            
                        </Grid>   
                        <Grid item container justify="flex-start" xs={12}>
                            <Button variant="contained" color="primary" onClick={e => this.handleFormSubmit(e)} >Guardar</Button>
                        </Grid>
                        {/*</form>*/}
                    </Grid>                    
                </Grid>
            </div>
        )
    }
}

export default Persona;
