import React from "react";

class Persona extends React.Component {
    
    state = {
        nombre: '',
        apellido: '',
        cuit: '',
        fecha_nacimiento: '',
        edad: '',
        telefono: '',
        email: '',
        calle: '',
        altura: '',
        barrio: '',
        es_beneficiario: '',
        tipo_beneficiario: '',
        tipos_de_beneficiarios: []
    };

    handleChangeBeneficiario = (e) => {
        const es_beneficiario = e.target.value;
        let tipos_de_beneficiarios;

        switch (es_beneficiario) {
            case "NO": tipos_de_beneficiarios = [{clave: "NO", valor: "No Corresponde"}];
                break;
            case "MUNI": tipos_de_beneficiarios = [ {clave: "BECA", valor: "Beca Estudiantil"}, {clave: "SUBS", valor: "Subsidio"}, {clave: "OTR", valor: "Otro"}];
                break;
            case "PROV": tipos_de_beneficiarios = [ {clave: "SUBS", valor: "Subsidio"}, {clave: "TARJ", valor: "Tarjeta Río Negro Presente"}, {clave: "SIP", valor: "Siprove"}, {clave: "OTR", valor: "Otro"}];
                break;
            case "NAC": tipos_de_beneficiarios = [ {clave: "AUH", valor: "Asignación Universal por Hijo"}, {clave: "DISC", valor: "Discapacidad"}, {clave: "ALIM", valor: "Alimentar"}, {clave: "PRO", valor: "Progresar"}, {clave: "PEN", valor: "Pensión"}];
                break;
        }

        this.setState({es_beneficiario: es_beneficiario, tipos_de_beneficiarios: tipos_de_beneficiarios});
    };

    handleChangeTipoBeneficiario = (e) => {
        this.setState({tipo_beneficiario: e.target.value})
    };

    handleFormSubmit = () => {
        
    };
    
    render() {
        return (
            <div className="App">
                <h2>Agregar Persona</h2>
                <div className="col2">
                    <form onSubmit={this.handleSubmit}>
                        <label>Nombre</label>
                        <input type="text" id="txtNombre" name="nombre" placeholder="Nombre" value={this.state.nombre} onChange={e => this.setState({ nombre: e.target.value })} />
                        <label>Apellido</label>
                        <input type="text" id="txtApellido" name="apellido" placeholder="Apellido" value={this.state.apellido} onChange={e => this.setState({ apellido: e.target.value })}/>
                        <label>CUIL / CUIT</label>
                        <input type="text" id="txtCuit" name="cuit" placeholder="CUIL / CUIT" value={this.state.cuit} onChange={e => this.setState({ cuit: e.target.value })}/>
                        <label>Fecha Nacimiento</label>
                        <input type="text" id="txtFechaNacimiento" name="fecha_nacimiento" placeholder="Fecha Nacimiento" value={this.state.fecha_nacimiento} onChange={e => this.setState({ fecha_nacimiento: e.target.value })}/>
                        <label>Edad</label>
                        <input type="text" id="txtEdad" name="edad" placeholder="Edad" value={this.state.edad} onChange={e => this.setState({ edad: e.target.value })}/>
                        <label>Teléfono</label>
                        <input type="text" id="txtTelefono" name="telefono" placeholder="Teléfono" value={this.state.telefono} onChange={e => this.setState({ telefono: e.target.value })}/>
                        <label>Mail</label>
                        <input type="text" id="txtEmail" name="email" placeholder="Email" value={this.state.email} onChange={e => this.setState({ email: e.target.value })}/>
                        <label>Calle</label>
                        <input type="text" id="txtCalle" name="calle" placeholder="Calle" value={this.state.calle} onChange={e => this.setState({ calle: e.target.value })}/>
                        <label>Altura</label>
                        <input type="text" id="txtAltura" name="altura" placeholder="Altura" value={this.state.altura} onChange={e => this.setState({ altura: e.target.value })}/>
                        <label>Barrio</label>
                        <input type="text" id="txtBarrio" name="barrio" placeholder="Barrio" value={this.state.barrio} onChange={e => this.setState({ barrio: e.target.value })}/>
                        <label>¿Es Beneficiario?</label>
                        <select id="ddlBeneficiario" name="es_beneficiario" value={this.state.es_beneficiario} onChange={this.handleChangeBeneficiario}>
                            <option value="NO">No corresponde</option>
                            <option value="MUNI">Municipal</option>
                            <option value="PROV">Provincial</option>
                            <option value="NAC">Nacional</option>
                        </select>
                        <label>Tipo Beneficio</label>
                        <select id="ddlTipoBeneficiario" name="tipo_beneficiario" value={this.state.tipo_beneficiario} onChange={this.handleChangeTipoBeneficiario}>
                            {
                                this.state.tipos_de_beneficiarios.map(data=><option key={data.clave} value={data.clave}>{data.valor}</option>)
                            }
                        </select>
                        <input type="button" value="Guardar" onClick={e => this.handleFormSubmit(e)} />
                    </form>
                </div>
            </div>
        )
    }
}

export default Persona;
